import React from "react";
import { Trophy, Loader2, CheckCircle, XCircle, Clock } from "lucide-react";
import { motion, AnimatePresence } from "framer-motion";
import Confetti from "react-confetti";

export default function AuctionLive() {
  const [stage, setStage] = React.useState("LIVE"); // LIVE â†’ RESULT
  const [allocations, setAllocations] = React.useState([]);
  const [countdown, setCountdown] = React.useState(8);
  const [didWin, setDidWin] = React.useState(false);

  // âœ… Confetti dimensions (auto-resize on window change)
  const [windowSize, setWindowSize] = React.useState({
    width: window.innerWidth,
    height: window.innerHeight,
  });

  React.useEffect(() => {
    const handleResize = () =>
      setWindowSize({ width: window.innerWidth, height: window.innerHeight });
    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  // Countdown timer
  React.useEffect(() => {
    if (stage === "LIVE" && countdown > 0) {
      const interval = setInterval(() => {
        setCountdown((c) => c - 1);
      }, 1000);
      return () => clearInterval(interval);
    }
    if (stage === "LIVE" && countdown === 0) {
      // simulate results
      const allocs = [
        { user: "You", qty: 12000, price: 102.5 },
        { user: "MM-Alpha", qty: 5000, price: 102.4 },
      ];
      setAllocations(allocs);
      setDidWin(allocs.some((a) => a.user === "You"));
      setStage("RESULT");
    }
  }, [stage, countdown]);

  if (stage === "LIVE") {
    return (
      <div className="p-8 text-center space-y-6">
        <motion.div
          animate={{ rotate: 360 }}
          transition={{ repeat: Infinity, duration: 1, ease: "linear" }}
        >
          <Loader2 className="mx-auto text-teal-600" size={50} />
        </motion.div>

        <h1 className="text-2xl font-bold">Auction in progressâ€¦</h1>
        <p className="text-gray-500 text-sm">
          Matching orders in micro-lots. Please wait.
        </p>

        {/* Countdown */}
        <div className="flex justify-center items-center gap-2 mt-4">
          <Clock className="text-gray-600" />
          <span className="font-mono text-lg">
            Results in <span className="font-bold">{countdown}</span>s
          </span>
        </div>

        {/* Progress bar */}
        <div className="w-full bg-gray-200 rounded-full h-2 mt-6">
          <motion.div
            className="bg-teal-600 h-2 rounded-full"
            initial={{ width: "0%" }}
            animate={{ width: `${((8 - countdown) / 8) * 100}%` }}
            transition={{ ease: "linear", duration: 1 }}
          />
        </div>
      </div>
    );
  }

  // ðŸŽ‰ Result Screen
  return (
    <div className="p-8 space-y-8 text-center relative">
      {/* ðŸŽŠ Confetti only if user won */}
      {didWin && (
        <Confetti width={windowSize.width} height={windowSize.height} />
      )}

      <AnimatePresence>
        {didWin ? (
          <motion.div
            initial={{ scale: 0.8, opacity: 0 }}
            animate={{ scale: 1, opacity: 1 }}
            className="flex flex-col items-center gap-2"
          >
            <Trophy className="text-yellow-500" size={50} />
            <h1 className="text-3xl font-bold text-teal-700">
              ðŸŽ‰ Congratulations! You Won Allocation
            </h1>
            <p className="text-gray-600">
              Your order was successfully matched in the auction.
            </p>
          </motion.div>
        ) : (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            className="flex flex-col items-center gap-2"
          >
            <XCircle className="text-red-500" size={50} />
            <h1 className="text-3xl font-bold text-red-600">
              ðŸ˜ž Better luck next time
            </h1>
            <p className="text-gray-600">
              Unfortunately, your order did not get allocation.
            </p>
          </motion.div>
        )}
      </AnimatePresence>

      {/* Allocation List */}
      <div className="bg-white rounded-xl border shadow-sm divide-y max-w-lg mx-auto">
        {allocations.map((a, i) => (
          <motion.div
            key={i}
            initial={{ x: -20, opacity: 0 }}
            animate={{ x: 0, opacity: 1 }}
            transition={{ delay: i * 0.2 }}
            className={`flex justify-between items-center p-4 ${
              a.user === "You" ? "bg-teal-50 border-l-4 border-teal-500" : ""
            }`}
          >
            <div>
              <div className="font-semibold">
                {a.user === "You" ? "ðŸ‘‰ You" : a.user}
              </div>
              <div className="text-xs text-gray-500">
                Qty {a.qty.toLocaleString()} Â· Price â‚¹{a.price}
              </div>
            </div>
            <CheckCircle
              className={a.user === "You" ? "text-teal-600" : "text-gray-400"}
            />
          </motion.div>
        ))}
      </div>

      {/* CTA Buttons */}
      <div className="flex justify-center gap-3">
        <button className="px-4 py-2 border rounded-lg hover:bg-gray-50">
          Download Contract
        </button>
        <button className="px-4 py-2 bg-teal-600 text-white rounded-lg hover:bg-teal-700">
          Verify Allocation
        </button>
      </div>
    </div>
  );
}
