import { useState } from "react";
import Sidebar from "./components/Sidebar";
import Header from "./components/homepage/Header";
import PortfolioSnapshot from "./components/homepage/PortfolioSnapshot";
import FeaturedBonds from "./components/homepage/FeaturedBonds";
import QuickActions from "./components/homepage/QuickActions";
import BondDiscovery from "./components/discoverPage/BondDiscovery";
import PortfolioDashboard from "./components/portfolioPage/PortfolioPage";
import LoginPage from "./components/auth/Login";
import SignupStepper from "./components/auth/SignUpPage";

function App() {
  const [currentPage, setCurrentPage] = useState("login");

  return (
    <div className="flex h-screen bg-gray-50">
      {/* Sidebar + Header only after login */}
      {currentPage !== "login" && currentPage !== "signup" && (
        <Sidebar currentPage={currentPage} onNavigate={setCurrentPage} />
      )}

      <div className="flex-1 flex flex-col">
        {currentPage !== "login" && currentPage !== "signup" && <Header />}
        <main className="p-6 space-y-6">
          {currentPage === "login" ? (
            <LoginPage
              onSignup={() => setCurrentPage("signup")}
              onLogin={() => setCurrentPage("home")}
            />
          ) : currentPage === "signup" ? (
            <SignupStepper onLogin={() => setCurrentPage("home")} />
          ) : currentPage === "home" ? (
            <>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <PortfolioSnapshot />
                <FeaturedBonds />
              </div>
              <QuickActions />
            </>
          ) : currentPage === "discover" ? (
            <BondDiscovery />
          ) : currentPage === "portfolio" ? (
            <PortfolioDashboard />
          ) : (
            <div className="text-gray-500">Coming soon...</div>
          )}
        </main>
      </div>
    </div>
  );
}

export default App;
