import React from "react";
import { ArrowLeft, Send, TrendingUp, Users, Info } from "lucide-react";
import { useNavigate } from "react-router-dom";

export default function RFQPage() {
  const navigate = useNavigate();
  const [form, setForm] = React.useState({ side: "BUY", qty: "", price: "" });
  const [rfqs, setRfqs] = React.useState([]);

  function handleSubmit(e) {
    e.preventDefault();
    if (!form.qty) return alert("Enter quantity");
    const newRfqs = [
      {
        id: `rfq-${Date.now()}`,
        side: form.side,
        qty: Number(form.qty),
        price: form.price ? Number(form.price) : null,
        ts: Date.now(),
      },
      ...rfqs,
    ];
    setRfqs(newRfqs);
    setForm({ side: "BUY", qty: "", price: "" });
  }

  return (
    <div className="p-6 space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <button
          onClick={() => navigate("/auctions")}
          className="flex items-center gap-2 text-gray-600 hover:underline"
        >
          <ArrowLeft size={16} /> Back
        </button>
        <h1 className="text-2xl font-bold text-slate-900">Request for Quote</h1>
      </div>

      {/* RFQ Form */}
      <div className="grid lg:grid-cols-3 gap-6">
        <div className="lg:col-span-1 bg-white rounded-xl border shadow-sm p-5">
          <h2 className="font-semibold mb-4">New RFQ</h2>
          <form onSubmit={handleSubmit} className="space-y-4">
            <div>
              <label className="text-xs text-gray-500">Side</label>
              <select
                value={form.side}
                onChange={(e) =>
                  setForm((s) => ({ ...s, side: e.target.value }))
                }
                className="w-full border rounded-lg px-3 py-2 text-sm"
              >
                <option value="BUY">Buy</option>
                <option value="SELL">Sell</option>
              </select>
            </div>

            <div>
              <label className="text-xs text-gray-500">Quantity (â‚¹)</label>
              <input
                type="number"
                value={form.qty}
                onChange={(e) =>
                  setForm((s) => ({ ...s, qty: e.target.value }))
                }
                className="w-full border rounded-lg px-3 py-2 text-sm"
                placeholder="Enter lot size"
              />
            </div>

            <div>
              <label className="text-xs text-gray-500">
                Limit Price (optional)
              </label>
              <input
                type="number"
                value={form.price}
                onChange={(e) =>
                  setForm((s) => ({ ...s, price: e.target.value }))
                }
                className="w-full border rounded-lg px-3 py-2 text-sm"
                placeholder="Leave blank for market RFQ"
              />
            </div>

            <button
              type="submit"
              className="w-full flex items-center justify-center gap-2 bg-teal-600 text-white py-2 rounded-lg hover:bg-teal-700"
            >
              <Send size={16} /> Submit RFQ
            </button>
          </form>
        </div>

        {/* Live Preview + Insights */}
        <div className="lg:col-span-2 space-y-6">
          <div className="bg-white rounded-xl border shadow-sm p-5">
            <h2 className="font-semibold mb-4">Live RFQs (Preview)</h2>
            {rfqs.length ? (
              <div className="divide-y">
                {rfqs.map((r) => (
                  <div
                    key={r.id}
                    className="flex items-center justify-between py-3"
                  >
                    <div>
                      <div className="flex gap-2 items-center">
                        <span
                          className={`px-2 py-1 text-xs rounded-lg ${
                            r.side === "BUY"
                              ? "bg-green-50 text-green-700"
                              : "bg-red-50 text-red-700"
                          }`}
                        >
                          {r.side}
                        </span>
                        <span className="font-medium">
                          {r.price ? `â‚¹${r.price}` : "Market"}
                        </span>
                      </div>
                      <div className="text-xs text-gray-500 mt-1">
                        Qty: {r.qty.toLocaleString()} Â· {timeAgo(r.ts)}
                      </div>
                    </div>
                    <button className="px-3 py-1 border text-xs rounded-lg">
                      Cancel
                    </button>
                  </div>
                ))}
              </div>
            ) : (
              <div className="text-gray-400 text-sm">No RFQs placed yet.</div>
            )}
          </div>

          {/* Insights */}
          <div className="grid md:grid-cols-3 gap-4">
            <InsightCard
              icon={<TrendingUp />}
              title="Market Depth"
              value="Strong"
            />
            <InsightCard icon={<Users />} title="Participants" value="24" />
            <InsightCard icon={<Info />} title="Auction Link" value="A-1" />
          </div>
        </div>
      </div>
    </div>
  );
}

function InsightCard({ icon, title, value }) {
  return (
    <div className="bg-white rounded-xl border shadow-sm p-4 flex gap-3 items-center">
      <div className="text-slate-600">{icon}</div>
      <div>
        <div className="text-xs text-gray-500">{title}</div>
        <div className="font-semibold">{value}</div>
      </div>
    </div>
  );
}

function timeAgo(ts) {
  const s = Math.floor((Date.now() - ts) / 1000);
  if (s < 60) return `${s}s ago`;
  if (s < 3600) return `${Math.floor(s / 60)}m ago`;
  return `${Math.floor(s / 3600)}h ago`;
}
