import React from "react";
import { Trophy, Loader2, CheckCircle } from "lucide-react";

export default function AuctionLive() {
  const [stage, setStage] = React.useState("LIVE"); // LIVE → RESULT
  const [allocations, setAllocations] = React.useState([]);

  React.useEffect(() => {
    if (stage === "LIVE") {
      // simulate auction ending
      setTimeout(() => {
        setAllocations([
          { user: "You", qty: 12000, price: 102.5 },
          { user: "MM-Alpha", qty: 5000, price: 102.4 },
        ]);
        setStage("RESULT");
      }, 8000);
    }
  }, [stage]);

  if (stage === "LIVE") {
    return (
      <div className="p-6 text-center">
        <Loader2 className="animate-spin mx-auto text-teal-600" size={40} />
        <h1 className="text-xl font-bold mt-4">Auction in progress…</h1>
        <p className="text-gray-500 text-sm">
          Orders being matched in micro-lots. Stay tuned!
        </p>
      </div>
    );
  }

  return (
    <div className="p-6 space-y-6">
      <div className="flex items-center gap-3">
        <Trophy className="text-yellow-500" size={28} />
        <h1 className="text-2xl font-bold">Auction Results</h1>
      </div>

      <div className="bg-white rounded-xl border shadow-sm divide-y">
        {allocations.map((a, i) => (
          <div key={i} className="flex justify-between items-center p-4">
            <div>
              <div className="font-semibold">{a.user}</div>
              <div className="text-xs text-gray-500">
                Qty {a.qty} · Price ₹{a.price}
              </div>
            </div>
            <CheckCircle className="text-green-500" />
          </div>
        ))}
      </div>

      <div className="flex gap-3">
        <button className="px-4 py-2 border rounded-lg">
          Download Contract
        </button>
        <button className="px-4 py-2 bg-teal-600 text-white rounded-lg hover:bg-teal-700">
          Verify Allocation
        </button>
      </div>
    </div>
  );
}
