import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import Sidebar from "./components/Sidebar";
import Header from "./components/homepage/Header";
import PortfolioSnapshot from "./components/homepage/PortfolioSnapshot";
import FeaturedBonds from "./components/homepage/FeaturedBonds";
import QuickActions from "./components/homepage/QuickActions";
import BondDiscovery from "./components/discoverPage/BondDiscovery";
import PortfolioDashboard from "./components/portfolioPage/PortfolioPage";
import LoginPage from "./components/auth/Login";
import SignupStepper from "./components/auth/SignUpPage";

// Layout wrapper (Sidebar + Header only for logged-in pages)
function AppLayout({ children }) {
  return (
    <div className="flex h-screen bg-gray-50">
      <Sidebar />
      <div className="flex-1 flex flex-col">
        <Header />
        <main className="p-6 space-y-6">{children}</main>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/login" element={<LoginPage />} />

        {/* 2️⃣ Signup Page */}
        <Route path="/signup" element={<SignupStepper />} />

        {/* 3️⃣ Home Page with Sidebar + Header */}
        <Route
          path="/home"
          element={
            <AppLayout>
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <PortfolioSnapshot />
                <FeaturedBonds />
              </div>
              <QuickActions />
            </AppLayout>
          }
        />

        {/* 4️⃣ Discover Page */}
        <Route
          path="/discover"
          element={
            <AppLayout>
              <BondDiscovery />
            </AppLayout>
          }
        />

        {/* 5️⃣ Portfolio Page */}
        <Route
          path="/portfolio"
          element={
            <AppLayout>
              <PortfolioDashboard />
            </AppLayout>
          }
        />
      </Routes>
    </Router>
  );
}

export default App;
